package Loops;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for syntax
		for(int i=0;i<10;i++) {
			System.out.println(i);
		}

		// initialization will be executed only once before execution of loop starts
		// condition will be executed till it fails start of every iteration
		// increment/decrement will be executed at the end of the every iteration
	}

}
