package Operator;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		a=20;
		float b=30f;
		System.out.println(a+b);
		
		// char addition
		int c=20;
		char ch='A';
		int d = c+ch;
		System.out.println(d);
		
		// int with long
		
		int i=45;
		long l =567;
		long j=i+l;
		System.out.println(i+l);
		
		// char with byte
		byte e = 12;
		char cr='A';//65
		//12+'A'
		char h = (char) (e+cr);
		System.out.println(e+cr);
		
		
		
		
		

	}

}
