package Operator;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		a+=10; // a = a+10
		// a = a+10;// 10+10 ==> 20
		// a+=10
		System.out.println(a);
		a-=5;  // a=a-5;
		// a = a-5;
		// 20-5 ==> 15
		
		System.out.println(a);
		a*=2; // a=a*2;
		
		System.out.println(a);
		a/=3; // a=a/3;
		
		System.out.println(a);
		
		a%=3; // a=a%3
		System.out.println(a);
		
		
		
		
	}

}
